# SELECT I.FLAVOR FROM ICECREAM_INFO I JOIN FIRST_HALF F ON FLAVOR WHERE SUM(TOTAL_ORDER) > 3000 AND I.INGREDIENT_TYPE = 'fruit_based' ORDER BY SUM(TOTAL_ORDER)
SELECT I.FLAVOR FROM ICECREAM_INFO I JOIN FIRST_HALF F ON I.FLAVOR = F.FLAVOR WHERE I.INGREDIENT_TYPE = 'fruit_based' AND F.TOTAL_ORDER > 3000 ORDER BY F.TOTAL_ORDER DESC