SELECT P.MEMBER_NAME, R.REVIEW_TEXT, DATE_FORMAT(R.REVIEW_DATE, '%Y-%m-%d') AS REVIEW_DATE FROM MEMBER_PROFILE AS P JOIN REST_REVIEW AS R USING (MEMBER_ID) WHERE P.MEMBER_ID = (SELECT MEMBER_ID FROM REST_REVIEW GROUP BY MEMBER_ID, MEMBER_NAME ORDER BY COUNT(*) DESC LIMIT 1) ORDER BY REVIEW_DATE, REVIEW_TEXT